mapper.update:
update filetemplate
set fileName = #{filename,jdbcType=VARCHAR},
  projectType = #{projecttype,jdbcType=VARCHAR},
  fileUrl = #{fileurl,jdbcType=VARCHAR},
  `enable` = #{enable,jdbcType=BIT}
where fileId = #{fileid,jdbcType=INTEGER}

mapper.select:
select fileId, fileName, projectType, fileUrl, `enable`
from filetemplate
where fileId = #{fileid,jdbcType=INTEGER}
