mapper.insert:
insert into wx_project_item (PROJECTID, PROCESSDEFID, PROJECTNAME, 
  PROJECTTYPE, PROJECTBUDGET, PROCESSINSTANCEID
  )
values (#{projectid,jdbcType=INTEGER}, #{processdefid,jdbcType=VARCHAR}, #{projectname,jdbcType=VARCHAR}, 
  #{projecttype,jdbcType=VARCHAR}, #{projectbudget,jdbcType=REAL}, #{processinstanceid,jdbcType=INTEGER}
  )

mapper.select:
select PROJECTID, PROCESSDEFID, PROJECTNAME, PROJECTTYPE, PROJECTBUDGET, PROCESSINSTANCEID
from wx_project_item
where PROJECTID = #{projectid,jdbcType=INTEGER}

mapper.update:
update wx_project_item
set PROJECTNAME = #{projectname,jdbcType=VARCHAR},
  PROJECTTYPE = #{projecttype,jdbcType=VARCHAR},
  PROJECTBUDGET = #{projectbudget,jdbcType=REAL}
where PROCESSINSTANCEID = #{processinstanceid}

